const timeZone = 'Europe/Lisbon';
const options = {
    timeZone: timeZone,
    year: 'numeric',
    month: 'long',
    day: 'numeric',
    weekday: 'long',
    hour: '2-digit',
    minute: '2-digit',
    second: '2-digit',
    hour12: true
};
const today = new Date();
const formattedDate = today.toLocaleString('pt-PT', options);
const result = {
    "formattedDate": formattedDate,
    "timezone": timeZone
};
return JSON.stringify(result);

const fetch = require('node-fetch');

const url = 'http://127.0.0.1:5678/webhook/69f93b22-0421-4435-aaa0-de386e99431c';
const token = $vars.Authorization; // variável definida no Flowise

console.log('Token:', token);

const options = {
    method: 'GET',
    headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${token}`
    }
};

try {
    const response = await fetch(url, options);
    const text = await response.text();
    console.log('Resposta bruta:', text);

    let json;
    try {
        json = JSON.parse(text);
    } catch (parseError) {
        console.error('Erro ao fazer parse do JSON:', parseError.message);
        throw new Error('Resposta inválida. Esperado JSON, recebido: ' + text);
    }

    console.log('Resposta JSON:', JSON.stringify(json, null, 2));

    // Verificação para garantir que a resposta contém um array com objetos no formato esperado
    if (Array.isArray(json) && json.every(item => item.id && item.nome && item.email && item.telefone)) {
        // Retorna os dados completos para o Flowise
        return json.map(item => ({
            id: item.id,
            nome: item.nome,
            email: item.email,
            telefone: item.telefone,
            data_nascimento: item.data_nascimento,
            endereco: item.endereco,
            criado_em: item.criado_em
        }));
    } else {
        console.warn('Formato inesperado na resposta JSON.');
        return [];
    }

} catch (error) {
    console.error('Erro no fetch:', error);
    return [];
}

